        [TestMethod]
        unsafe public void TestRSV_FR()
        {
            foreach (String rawLine in FR_RAW.Keys)
            {
                var ffxivMock = Helper.MockFFXIVRepository<LineRSV>();
                ffxivMock.Setup(ffxiv => ffxiv.GetLocaleString()).Returns("fr");
                byte[] rawData = Helper.ReadRawPacketLine(rawLine, out DateTime dt, true);
                String outputString;
                fixed (byte* data = rawData)
                {
                    Server_MessageHeader_Global hdr = Marshal.PtrToStructure<Server_MessageHeader_Global>(new IntPtr(data));
                    LineRSV.RSV_v62 rsv = Marshal.PtrToStructure<LineRSV.RSV_v62>(new IntPtr(data) + 0x20);
                    outputString = rsv.ToString(0, hdr.ActorID);
                }
                String[] parts = outputString.Split('|');
                String[] expectedParts = FR_RAW[rawLine];
                CollectionAssert.AreEqual(expectedParts, parts);
            }
        }
